import styled from 'styled-components';

const InputWrapper = styled.label`
    position: relative;

`;

const input = styled.input`
    position:absolute;
    left: -9999 px;
    top: -9999 px;

    &:checked + span {
        background-color: #2CEAA3;
    }
`;

const Slider = styled.span`
    display:flex;
    cursor: pointer;
    width: 50px;
    height: 25px;
    border-radius: 100px;
    background-color: #2CEAA3;
    position: relative;
    transition: background-color 0.2s;

    &:before {
        content: "";
        position: absolute;
        top: 2px;
        left: 2px;
        width: 21px;
        height: 21px;
        border-radius: 45px;
        transition: 0.2s;
        background: #2A6041;
        box-shadow: 0 2px 4px 0 rgba(0, 35, 11, 0.2);
    }
`

const ToggleSwitch = ({onChange}) => (
    <InputWrapper>
        <input type="checkbox" onChange={onChange}/>
        <Slider />
    </InputWrapper>
);

export default ToggleSwitch
